import React, {useState} from "react";
import {useNavigate} from "react-router-dom"
import Card from "../Components/Card";
import Input from "../Components/Input";
import NotRobot from "../Components/NotRobot";
import LoginButton from "../Components/LoginButtons";
import ErrorMessage from "../Components/ErrorMessage";
import useJWT from "../Hooks/useJWT"


const Login = (props) => {
        const navigate = useNavigate();
        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        const [data, setData] = useState([""]);
        const [noRobot, setNORobot] = useState(true);
        const [loggedIn, setLoggedIn] = useState(false);
        const {login} = useJWT();
        const clickHandler = async () => {
            const accessData = await login(email, password);
            console.log(accessData);
            if (accessData) {
                setData(accessData);
                setLoggedIn(true);
                navigate("/");
            }
            if (loggedIn === false) {
                setPassword(" ");
                setNORobot(false);
            }
        }
        return (
            <Card>
                <h3>Login</h3>

                <Input
                    data-testid="email"
                    label="âœ‰ï¸ Email"
                    type="email"
                    value={email}
                    onChange={(e) =>
                        setEmail(e.target.value)
                    }
                />

                <Input
                    data-testid="password"
                    label="ðŸ”‘ Password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {!data &&
                <>
                    <NotRobot checked={noRobot} onChange={() => {
                    }}/>
                    <ErrorMessage/>
                </>}
                <LoginButton onClick={clickHandler} disabled={!(password && email)}/>
            </Card>
        );
    }
;

export default Login;
