import React, {useState} from "react";
import {useNavigate} from "react-router-dom"
import Card from "../Components/Card";
import Input from "../Components/Input";
import NotRobot from "../Components/NotRobot";
import LoginButton from "../Components/LoginButtons";
import ErrorMessage from "../Components/ErrorMessage";
import useJWT from "../Hooks/useJWT"


const Login = (props) => {
        const navigate = useNavigate();
        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        const [data, setData] = useState([]);
        const [noRobot, setNORobot] = useState(true);
        const [loggedIn, setLoggedIn] = useState(true);
        const {login} = useJWT();

        function validateEmail(email) {
            let regexEmail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
            if (email.match(regexEmail)) {
                return true;
            } else {
                return false;
            }
        }

        // let emailAdress = "user@test.com";
        // console.log(validateEmail(emailAdress));

        const clickHandler = async () => {
            try {
                if (validateEmail(email) === true) {
                    const accessData = await login(email, password);
                    if (accessData) {
                        setData(accessData);
                        setLoggedIn(true);
                        navigate("/");
                        console.log(accessData);
                    }
                }
            } catch (err) {
                setPassword("");
                setNORobot(true);
                setLoggedIn(false);
            }
        }


        return (
            <Card>
                <h3>Login</h3>

                <Input
                    data-testid="email"
                    label="âœ‰ï¸ Email"
                    type="email"
                    value={email}
                    onChange={(e) =>
                        setEmail(e.target.value)
                    }
                />

                <Input
                    data-testid="password"
                    label="ðŸ”‘ Password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {loggedIn === false &&
                <>
                    <NotRobot checked={noRobot} onChange={() => {
                    }}/>
                    <ErrorMessage/>
                </>}
                <LoginButton onClick={clickHandler} disabled={!(password && email)}/>
            </Card>
        );
    }
;

export default Login;
